mysql_query("INSERT INTO paddata VALUES ('IM-Soft', 'Easy File Protector', '4.21', '2006-12-15', 'http://www.softstack.com', 'softstack@bigfoot.com', 29.00, 'Shareware', 'Win98,WinME,WinNT 3.x,WinNT 4.x,Windows2000,WinXP,Windows2003,Windows Vista Star', 677, 'Security & Privacy::Encryption Tools', 'password,security,access,control,protect,privacy,restrict,restrictions,restricting,computer,download,free,screen,lock', 'Easy File Protector is a very flexible password protected security utility that restricts access to your files and folders by user-defined time schedule. You choose users, define restricted files/folders and time periods for them, and activate the program. After that, your system disables/enables your files automatically depending on current time and user. Protection is achieved by making files and folders undeletable, unrenamable, unreadable, un', 'http://www.softstack.com/fileprot.html', 'http://www.softstack.com/images/fileprotpro.gif', 'http://www.softstack.com/pad/fileprot.xml', 'http://www.softstack.com/download/fileprot.zip', 'none', 0)", $link_id);
mysql_query("INSERT INTO paddata VALUES ('IM-Soft', 'Email Privacy', '2.71', '2006-12-15', 'http://www.softstack.com', 'softstack@bigfoot.com', 49.00, 'Shareware', 'Win98,WinME,WinNT 3.x,WinNT 4.x,Windows2000,WinXP,Windows2003,Windows Vista Star', 1189, 'Communications::E-Mail Clients', 'mail,mailing,email,e-mail,mass,bulk,mailer,address,book,remailer,download,free,bomber,smtp', 'Did you know that when you send your email messages, they do not go directly to the recipient mailboxes? Did you know that first of all, your Internet Service Provider gets all your email messages, stores them somewhere on its mail servers and then delivers the messages to your recipients leaving copies of your messages in its database? Do you know that someday all the information kept on the servers can be easily used against you?', 'http://www.softstack.com/emlpriv.html', 'http://www.softstack.com/images/emlpriv.gif', 'http://www.softstack.com/pad/emlpriv.xml', 'http://www.softstack.com/download/emlpriv.zip', 'none', 0)", $link_id);
