mysql_query("INSERT INTO paddata VALUES ('Photo Screensavers', 'Galleon 3D Photo Screensaver', '1.0', '2005-10-14', 'http://www.photoscreensavers.us', 'miroslav@photoscreensavers.us', 12.95, 'Shareware', 'Win98,WinME,Windows2000,WinXP', 5738, 'Desktop::Screen Savers: Nature', 'galleon 3d,3d screensaver,3d photo screensaver, galleon 3d screensaver', 'This beautiful galleon from the Age of Great Explorations is crossing the dangerous ocean at full sail again. Through blistering sun and quiet nights blessed by the moonshine, through fierce gun battles and storms, it rushes to new, yet to be discovered, lands. Raise your flag, captain, and enter the history. This was the ship all famous pirates dreamed about.', 'http://www.photoscreensavers.us/3d.html', 'http://www.photoscreensavers.us/galleon_shot01low.jpg', 'http://www.photoscreensavers.us/Galleon3d.xml', 'http://www.photoscreensavers.us/galleon.exe', 'none', 0)", $link_id);
